#!/bin/bash

# The "BLOB" below is a tar.xz file containing a (static) neofetch config and a small png tile file
BLOB=""

if [ $UID -ne 0 ]; then
	echo "[-] Need root permissions to run this script. Aborted" >&2
	exit 1
fi

LOGINBGNDSH="login-bgnd.sh"
if [ ! -r "$LOGINBGNDSH" ]; then
	echo "[-] Missing $LOGINBGNDSH script. Aborted" >&2
	exit 1
fi
chmod 755 "$LOGINBGNDSH"
mkdir -p /root/bin &>/dev/null
cp "$LOGINBGNDSH" /root/bin || \
	{ echo "[-] Failed to copy $LOGINBGNDSH to /root/bin. Aborted" >&2; exit 1; }

echo "[+] Installing prerequisite binaries..."
apt-get -y install git build-essential cmake libgd-dev neofetch imagemagick

echo "[+] Cloning libansilove..."
cd
git clone https://github.com/ansilove/libansilove.git

cd libansilove || \
	{ echo "[-] libansilove is not accesible. Aborted" >&2; exit 1; }

(
	mkdir build
	cd build
	cmake ..
	make
	make install
) || \
	{ echo "[-] Something went wrong while compiling and installing libansilove. Aborted" >&2; exit 1; }

echo "[+] Cloning libansilove..."
cd
git clone https://github.com/ansilove/ansilove.git

cd ansilove || \
	{ echo "[-] ansilove is not accesible. Aborted" >&2; exit 1; }

(
	mkdir build
	cd build
	cmake ..
	make
	make install
) || \
	{ echo "[-] Something went wrong while compiling and installing ansilove. Aborted" >&2; exit 1; }

ldconfig

echo "[+] Modifying lightdm..."

sed -i 's@^\(display-setup-script.*=.*$\)@#\1@;s@^\(#display-setup-script.*\)$@\1\ndisplay-setup-script=/root/bin/login-bgnd.sh@' /etc/lightdm/lightdm.conf

ROWS="$(grep '^display-setup-script' /etc/lightdm/lightdm.conf | wc -l)"

if [ "$ROWS" -ne 1 ]; then
	echo "[i] /etc/lightdm/lightdm.conf has an unexpected number of display-setup-scripts configured. Please check."
fi

cd
mkdir images &>/dev/null
cd images || \
	{ echo "[-] Can't access images directory. Aborted" >&2; exit 1; }

echo $BLOB | base64 -d | tar Jxf -
mkdir -p /etc/neofetch/ &>/dev/null
mv config.conf /etc/neofetch || \
	{ echo "[-] Failed to install neofetch configuration. Aborted" >&2; exit 1; }
cd

echo "[+] Done. Reboot to see the effect."

